package week1.day1;

public class NegPosScenerio {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 48;
		if (a > 0) {
			System.out.println("The Number is Postive "+a);
			
		}
		else if(a < 0)
		{
			System.out.println("The Number is Negative "+a);
		}
		else
		{
			System.out.println("The Number is Neither Negative Nor Postive "+a);
		}
		
		
	}
	
}
